                                    if (this.outputformat === "timestamp") {
                                        return timeValue;
                                    }
                                    if (!this.outputformat) {
                                        this.outputformat = 'HH:mm:ss';
                                    }
                                    return timeValue ? $filter('date')(timeValue, this.outputformat) : undefined;
