                    'onClick'           : $is.onClick,
                    'onMouseenter'      : $is.onMouseenter,
                    'onMouseleave'      : $is.onMouseleave,
                    'onEnterkeypress'   : $is.onEnterkeypress,
                    'onSetrecord'       : $is.onSetrecord,
                    'itemclass'         : $is.itemclass,
                    'itemsPerRowClass'  : getRowClass(attrs.itemsperrow)
