                prefabMethodsMap[iScope.prefabname] = methodsMap;

                iScope.widgetProps = WM.copy(prefabWidgetPropsMap[iScope.prefabname]);
                iScope._methodsMap = prefabMethodsMap[iScope.prefabname];
